import React, { Component } from 'react'
import {parse, formatTime, formatResultNum, sort, filter, getVideoData} from './functions'
import queryData from './data/tests.json'

import styled from 'styled-components'
import {Comment} from '@styled-icons/boxicons-solid/Comment'
import {Play} from '@styled-icons/boxicons-regular/Play'
import Moment from 'react-moment'
import 'bootstrap/dist/css/bootstrap.min.css';
import { Row, Col, Container } from 'react-bootstrap'
import { createBrowserHistory } from "history"




export class Serp extends Component {
    constructor(props) {
        super(props)

        this.state = {
            value: '',
            currentSearchParam: null,
            resultNum: null,
            hasBeenSearched: false,
            dateFilter: "anyDate",
            durationFilter: "anyDuration",
            featuredFilter: "anyFeatured",
            currentSort: null,
            filtered: false,
            smallMenu: false,
            results: [],
            filteredResults: []
        }
    }

    componentDidMount() {
        this.checkURL()
    }

    checkURL = async () => {
        const currentURLParam = window.location.search.slice(3)
        if(currentURLParam !== this.state.currentSearchParam) {
            this.setState({
                currentSearchParam: currentURLParam,
                hasBeenSearched: true,
                results: await getVideoData(currentURLParam), 
                resultNum: await formatResultNum(currentURLParam)
            })
        }
    }

    onInputChange = (e) => {
        this.setState({ value: e.target.value })
    }

    onSubmit = async (e) => {
        e.preventDefault()
        const browserHistory = createBrowserHistory()

        if(this.state.value.length === 0) {
            alert ('Redirected to https://vimeo.com/search?q=')
        } else {
            this.setState({
                value: '',
                currentSearchParam: this.state.value,
                // hasBeenSearched: true,
                results: await getVideoData(this.state.value), 
                resultNum: await formatResultNum(this.state.value)
            })
        }
        browserHistory.push(`?q=${this.state.currentSearchParam}`)
        this.updateResults()
    }

    onSortChange = async (e) => {
        if(!this.state.results) {
            this.setState({currentSort: e.target.value})
        } else {
            this.setState({
                results: await sort(this.state.results, e.target.value),
                currentSort: e.target.value
            })
            this.updateResults()
        }
    }


    onDateFilterChange = async (e) => {
        await this.setState({
            dateFilter: e.target.value,
            filtered: true
        })
        this.updateResults()
    }

    onDurationFilterChange = async (e) => {
        await this.setState({
            durationFilter: e.target.value,
            filtered: true
        })
        this.updateResults()
    }

    onFeaturedFilterChange = async (e) => {
        await this.setState({
            featuredFilter: e.target.value,
            filtered: true
        })
        this.updateResults()
    }

    updateResults = () => {
        this.setState({
            results: sort(this.state.results, this.state.currentSort),
            filteredResults: sort(filter(this.state.results, this.state.dateFilter, this.state.durationFilter, this.state.featuredFilter), this.state.currentSort),
        })
    }

    componentDidUpdate() {      
        if( this.state.durationFilter === "anyDuration" &&
            this.state.featuredFilter === "anyFeature" &&
            this.state.dateFilter === "anyDate") {
                this.setState({
                    filtered: false
                })
            }
        this.checkURL()
    }

    openSmallFilters = () => {
        this.setState({
            smallMenu: true
        })
    }

    closeSmallMenu = () => {
        this.setState({
            smallMenu: false
        })
    }

    render() {

        const Button = styled.button`
            background: transparent;
            border-radius: 3px;
            border: 2px solid #59b2ed;
            color: #59b2ed;
            margin: 0 1em;
            padding: 0.25em 1em;
        `
        const Button2 = styled.button`
            background: transparent;
            border-radius: 3px;
            border: 2px solid #59b2ed;
            color: #59b2ed;
            padding: 0.25em 1em;
        `
        const Whitecomment = styled(Comment)`
            padding-right: 5px; 
            color: white;
            height: 60%;
            width: auto;
        `
       const Whiteplay = styled(Play)`
            padding-bottom: 3px;
            color: white;
            height: 100%;
            width: auto;
        `

        const hasBeenSearched = this.state.hasBeenSearched
        const param = this.state.currentSearchParam 
        const resultNum = this.state.resultNum
        const smallMenu = this.state.smallMenu
        const results = (this.state.filtered) ? this.state.filteredResults : this.state.results
        const searched = (hasBeenSearched) ? <h5> {resultNum} results for "{param}" </h5> : <span>  </span>
        const smallFilterMenu = (smallMenu) ? "menu-change" : "menu-change hide"


        const resultsMap = (!results || results.length === 0 && (hasBeenSearched)) ? <h5> we got nothin' for ya ;-( </h5> : results.map((video) => {
            const duration = formatTime(video.duration)
            const featured = (video.featured) ? "featured result-box" : "result-box"
            return (
                <Col xl={4} lg={4} md={6} sm={6} xs={12} className="p-0 m-0" key={video.videoID}> 

                    <div className={featured}>  
                        <div className="thumbnail-wrapper">                        
                            <div className="detail-container"> 
                                <div className="hover-details">
                                    <div className="ago"> 
                                        <span> <Moment fromNow ago>{video.created}</Moment> ago </span>
                                    </div>

                                    <div className="plays"> 
                                        <Whiteplay />
                                        <span>{video.plays}</span>
                                    </div>

                                    <div className="comments">
                                        <Whitecomment />
                                        <span>{video.comments}</span>
                                    </div>

                                    <div className="duration">
                                        <span>{duration}</span>
                                    </div>
                                </div>

                            </div>

                            <a href={video.URLformat}>
                                <img src={video.pictures} className="thumbnail" alt="thumbnail"/>
                            </a>

                        </div>
                        <div className="info-container"> 
                            <div className="video-title"> 
                                <a href={video.URLformat} target="_blank">{video.name}</a> 
                            </div>
                            <div className="user-link"> 
                                <a href={video.userLink}> {video.user}</a> 
                            </div>   
                        </div>
                    </div>
                </Col>
            )
        })

        const filterMenu = 
                        <React.Fragment>
                            <div className="filter-options"> 
                                <h4>Filter by:</h4>
                                    <h6> Data Uploaded </h6>
                                        <ul>
                                            <form onChange={this.onDateFilterChange}>
                                                <li> <input type="radio"  className="filter-radio" name="date" value="anyDate" defaultChecked/> Any     </li>
                                                <li> <input type="radio" className="filter-radio" name="date" value="365" /> Last 365 Days               </li>
                                                <li> <input type="radio"  className="filter-radio" name="date" value="30"/> Last 30 Days                </li>
                                                <li> <input type="radio" className="filter-radio" name="date" value="7"/> Last 7 Days                   </li>
                                            </form>
                                        </ul>
                                    <h6> Duration </h6>
                                        <ul>
                                            <form onChange={this.onDurationFilterChange}>
                                                <li> <input type="radio" className="filter-radio" name="duration" value="anyDuration" defaultChecked/> Any  </li>
                                                <li> <input type="radio" className="filter-radio" name="duration" value="short"/> Short (&lt;4:00)          </li>
                                                <li> <input type="radio" className="filter-radio" name="duration" value="med"/> Med. (4 - 10:00)            </li>
                                                <li> <input type="radio" className="filter-radio" name="duration" value="long"/> Long (>10:00)              </li>
                                            </form>
                                        </ul>
                                    <h6> Staff Picks </h6>
                                        <ul>
                                            <form onChange={this.onFeaturedFilterChange}>
                                                <li> <input type="radio" className="filter-radio" name="featured" value="anyFeatured" defaultChecked/> Any  </li>
                                                <li> <input type="radio" className="filter-radio" name="featured" value="featured"/> Featured               </li>
                                            </form>
                                        </ul>
                                <Button2  className="collapse-button" onClick={this.closeSmallMenu}> X </Button2>
                            </div>
                        </React.Fragment>

        return (
            <Container fluid> 
                <Row className="header">
                    <Col md= {3} sm={0} xs={0}>
                        {/* filler col space  */}
                    </Col>

                    <Col xl={8} lg={9} md= {9} sm={12} xs={12}>

                        <div className="search-box">
                                <form onSubmit={this.onSubmit}> 
                                    <input  onChange={this.onInputChange}
                                            autoFocus
                                            id="input-field"
                                            placeholder="Search videos, people, and more"
                                            type="text"
                                            value={this.state.value}
                                            autoComplete="off"
                                            maxLength="18"/>
                                    <Button onSubmit={this.onSubmit}>Search</Button>
                                </form>
                        </div>

                        <div className="searched"> 
                            {searched} 
                        </div>


                    </Col>

                    <Col xl={1} lg={0}>
                        {/* filler col space */}
                    </Col>
                </Row>

                <Row>
                    <Col xl ={3} lg={3} md= {3} sm={0} xs={0} className="small-no-padding"> 

                        <Container fluid className="sort-options">
                            <div> 
                                <Button2 className="filter-show" onClick={this.openSmallFilters}>
                                    Filters
                                </Button2 >
                            </div>
                            <div> 
                            <h4>Sort by:</h4>
                                <select onChange={this.onSortChange} id="sort-menu">
                                    <option value="" disabled selected> Choose...</option>
                                    <option value="Popularity">Popularity</option>
                                    <option value="TitleAZ">Title (A-Z)</option>
                                    <option value="TitleZA">Title (Z-A)</option>
                                    <option value="Longest">Longest</option>
                                    <option value="Shortest">Shortest</option>
                                    <option value="Newest">Date uploaded (newest)</option>
                                    <option value="Oldest">Date uploaded (oldest)</option>
                                    <option value="UserAZ">Username (A-Z)</option>
                                    <option value="UserZA">Username (Z-A)</option>
                                </select>
                            </div>
                        </Container>

                        <Container className={smallFilterMenu}>  
                            {filterMenu}
                        </Container>
                    </Col> 

                    <Col xl={8} lg={9} md= {9} sm={12} xs={12}> 
                        <Row>
                            {resultsMap}
                        </Row>
                    </Col>

                    <Col xl={1} lg={0} md= {0} sm={0} xs={0}> 
                         {/* filler space col  */}
                    </Col>
                </Row>
            </Container>
        )
    }
}

export default Serp
