import React, { Component } from 'react'
import styled from 'styled-components'

export class Serp extends Component {
    constructor(props) {
        super(props)

        this.state = {
             formInput: null,
             currentSearchParam: null,
             results: null,
             hasBeenSearched: false
        }
    }

    // onChange = (e) => {
    //     this.setState({ formInput: e.target.value})
    // }

    onSubmit = (e) => {
        e.preventDefault()
        console.log(e.target.valye)
        this.setState({currentSearched:e.target.value})
    }


    render() {

        const Button = styled.button`
            background: transparent;
            border-radius: 3px;
            border: 2px solid palevioletred;
            color: palevioletred;
            margin: 0 1em;
            padding: 0.25em 1em;
            `

        const searched = (this.state.hasBeenSearched) ? <div className="result-summary"> {this.state.results} results for "{this.state.currentSearched}"</div> : null

        return (
            <div className="container">
                <div className="header">
            <div className="result-summary"> {this.state.results} results for "{this.state.currentSearched}"</div>
                    <div className="search-box">
                        <form > 
                            <input  onSubmit={this.onSubmit}
                                    id="input-field"
                                    placeholder="What sounds interesting?"
                                    type="text"
                                    value={this.state.searched}/>
                        </form>

                        <Button>hello</Button>
                    </div>
                </div>

                <div className="result-body">

                </div>
            </div>
        )
    }
}

export default Serp
