import React, { Component } from 'react'
import styled from 'styled-components'
import {Comment} from '@styled-icons/boxicons-solid/Comment'
import {Play} from '@styled-icons/boxicons-regular/Play'
import queryData from './data/animationP.json'
import Moment from 'react-moment'
import {parse, formatTime} from './functions'


export class Serp extends Component {
    constructor(props) {
        super(props)

        this.state = {
             value: '',
             currentSearchParam: null,
             resultNum: 9000,
             hasBeenSearched: false,
             results: []
        }
    }

    onChange = (e) => {
        this.setState({ value: e.target.value})
    }

    onSubmit = async (e) => {
        e.preventDefault()
        if(this.state.value.length === 0) {
            alert('Redirected to https://vimeo.com/search?q=')
        } else {

            // console.log(queryData['data'])


            this.setState({
                currentSearchParam:this.state.value,
                hasBeenSearched: true,
                results: parse(queryData)
            })
        }
    }

    parse = (response) => {
        const videoArr = []

        response["data"].forEach((video) => {
            const largestImage = video["clip"]["pictures"]["sizes"].length - 1
            videoArr.push({
                name: video["clip"].name,
                description: video["clip"].description,
                uri: video["clip"].uri,
                videoID: video["clip"].uri.slice(8), //this may be redundant as I already have the uri, but for ease of access and for unique map key, its here
                created: video["clip"].created_time,
                isFeatured: video.is_featured,
                URLformat: "https://vimeo.com/" + video["clip"].uri.slice(8),
                language: video["clip"].language,
                duration: video["clip"].duration, 
                plays: video["clip"]["stats"].plays,
                comments: video["clip"]["metadata"]["connections"]["comments"].total,
                user: video["clip"]["user"].name,
                userLink: video["clip"]["user"].link,
                pictures: video["clip"]["pictures"]["sizes"][largestImage].link
            })
        })
        return videoArr
    } 

    sort = (response) => {

    }

    render() {

        const Button = styled.button`
            background: transparent;
            border-radius: 3px;
            border: 2px solid palevioletred;
            color: palevioletred;
            margin: 0 1em;
            padding: 0.25em 1em;
        `
        const Whitecomment = styled(Comment)`
            padding-right: 5px; 
            color: white;
            height: 60%;
            width: auto;
        `

        const Whiteplay = styled(Play)`
            padding-bottom: 3px;
            color: white;
            height: 100%;
            width: auto;
        `

        const hasBeenSearched = this.state.hasBeenSearched
        const param = this.state.currentSearchParam 
        const resultNum = this.state.resultNum
        const results = this.state.results


        let resultsMap = results.map((video) => {
            const duration = formatTime(video.duration)
            return (
                    <div className="result-box container" key={video.videoID}>  
                        <div className="thumbnail-wrapper">
                            <div className="detail-container"> 
                                <div className="hover-details">

                                    <div className="ago"> 
                                        <span> <Moment fromNow ago>{video.created}</Moment> ago </span>
                                    </div>


                                    <div className="plays"> 
                                        <Whiteplay />
                                        <span>{video.plays}</span>
                                    </div>


                                    <div className="comments">
                                        <Whitecomment />
                                        <span>{video.comments}</span>
                                    </div>

                                    <div className="duration">
                                        <span>{duration}</span>
                                    </div>

                                </div>
                            </div>

                            <a href={video.URLformat}>
                                <img src={video.pictures} className="thumbnail" alt="thumbnail"/>
                            </a>

                        </div>

                        <div className="info-container"> 
                            <div className="video-title"> 
                                <a href={video.URLformat} target="_blank">{video.name}</a> 
                            </div>
                            <div className="user-link"> 
                                <a href={video.userLink}> {video.user}</a> 
                            </div>   
                        </div>
                    </div>)
        })

        let searched = (hasBeenSearched) ? <span className="result-summary"> {resultNum} results for "{param}" </span> : <span>  </span> 

        return (
            <div className="container">
                <div className="header">
                        {searched} 
                    <div className="search-box">
                        <form onSubmit={this.onSubmit}> 
                            <input  onChange={this.onChange}
                                    id="input-field"
                                    placeholder="Search videos, people, and more"
                                    type="text"
                                    value={this.state.value}/>
                            <Button onSubmit={this.onSubmit}>hello</Button>
                        </form>
                    </div>
                </div>

                <div className="result-body">
                    {resultsMap}
                </div>
            </div>
        )
    }
}

export default Serp
