export async function parse(response){

    const videoArr = []

    await response["data"].forEach((video) => {

        const clip = video["clip"] 
        const largestImage = clip["pictures"]["sizes"].length - 1

        videoArr.push({
            name: clip.name,
            description: clip.description,
            uri: clip.uri,
            videoID: clip.uri.slice(8), //this may be redundant as I already have the uri, but for ease of access and for unique map key, its here
            created: clip.created_time,
            isFeatured: video.is_featured,
            URLformat: "https://vimeo.com/" + video["clip"].uri.slice(8),
            language: clip.language,
            duration: clip.duration, 
            plays: clip["stats"].plays,
            comments: clip["metadata"]["connections"]["comments"].total,
            user: clip["user"].name,
            userLink: clip["user"].link,
            pictures: clip["pictures"]["sizes"][largestImage].link
        })
    })
    return videoArr
}   

