import React, { Component } from 'react'
import styled from 'styled-components'
import queryData from './data/animationP.json'


export class Serp extends Component {
    constructor(props) {
        super(props)

        this.state = {
             value: '',
             currentSearchParam: null,
             resultNum: 9000,
             hasBeenSearched: false,
             results: []
        }
    }

    onChange = (e) => {
        this.setState({ value: e.target.value})
    }

    onSubmit = (e) => {
        e.preventDefault()
        if(this.state.value.length === 0) {
            alert('Redirected to https://vimeo.com/search?q=')
        } else {

            // console.log(queryData['data'])
            console.log(queryData)
            console.log(this.parse(queryData))

            this.setState({
                currentSearchParam:this.state.value,
                hasBeenSearched: true,
                results: this.parse(queryData)
            })
        }
    }

    // parse = (response) => {
    //     const dictionary = {}
    //     response["data"].forEach((video) => {
    //         dictionary.videos = {
    //             name: video["clip"].name,
    //             description: video["clip"].description,
    //             uri: video["clip"].uri,
    //             created: video["clip"].created_time,
    //             isFeatured: video.is_featured
    //         }
    //     })
    //     return dictionary
    // }

    parse = (response) => {
        const videoArr = []
        response["data"].forEach((video) => {
            videoArr.push({
                name: video["clip"].name,
                description: video["clip"].description,
                uri: video["clip"].uri,
                created: video["clip"].created_time,
                isFeatured: video.is_featured,
                URLformat: "vimeo.com/" + video["clip"].uri.slice(8)
            })
        })
        return videoArr
    } 

    // queryBuilder = (param, filterOption, sortOption) => {

    // } 

    sort = (response) => {

    }

    render() {

        const Button = styled.button`
            background: transparent;
            border-radius: 3px;
            border: 2px solid palevioletred;
            color: palevioletred;
            margin: 0 1em;
            padding: 0.25em 1em;
            `

        const hasBeenSearched = this.state.hasBeenSearched
        const param = this.state.currentSearchParam
        const resultNum = this.state.resultNum
        const results = this.state.results


        let resultsMap = results.map((video) => {
            return (
                    <div 
                    key={video.uri}>  
                   <h1> {video.name} </h1> 
                    <a target="_blank"> VIDEO LINK </a>
                    </div>)
        })

        let searched = (hasBeenSearched) ? <span className="result-summary"> {resultNum} results for "{param}" </span> : <span>  </span> 


        return (
            <div className="container">
                <div className="header">
                        {searched} 
                    <div className="search-box">
                        <form onSubmit={this.onSubmit}> 
                            <input  onChange={this.onChange}
                                    id="input-field"
                                    placeholder="Search videos, people, and more"
                                    type="text"
                                    value={this.state.value}/>
                            <Button onSubmit={this.onSubmit}>hello</Button>
                        </form>


                    </div>
                </div>

                <div className="result-body">
                    {resultsMap}
                </div>
            </div>
        )
    }
}

export default Serp
