        "animation": animationData,
        "travel": travelData,
        "example": exampleData,  
    }

    const resultArr = []

    if(table[param.toLowerCase()] === undefined) return false
    await table[param.toLowerCase()]["data"].forEach((video) => {

        const clip = video["clip"] 
        const largestImage = clip["pictures"]["sizes"].length - 1

