        }
        if(currentFilter === "30") {
           return currentResults.filter(result => rightNow - Date.parse(result.created) <  month)
        }
        if(currentFilter === "7") {
          return currentResults.filter(result => rightNow - Date.parse(result.created) <  week)
        }
