import animationData from './data/animation.json'
import travelData from './data/travel.json'
import exampleData from './data/example.json'

// lookup table was used for quickly testing and rendering data. Once the Fuzzy Search was implemented, 
// I only kept this to show

const table = {
    "animation": animationData,
    "travel": travelData,
    "example": exampleData,  
}

export function formatData(video) {
    const clip = video["clip"] 
    const largestImage = clip["pictures"]["sizes"].length - 1

    // I felt that re-writing the data in this object array format helped me reason more clearly when mapping
    // and generally manipulating the data for rendering. I could have simply worked with JSON data as it
    // came from the example files, but felt that would be overly verbose and opted for a quick reorginization.

    return {
        name: clip.name,
        description: clip.description,
        uri: clip.uri,
        videoID: clip.uri.slice(8), 
        created: clip.created_time, 
        featured: video.is_featured,
        URLformat: "https://vimeo.com/" + video["clip"].uri.slice(8),
        language: clip.language,
        duration: clip.duration, 
        plays: clip["stats"].plays,
        comments: clip["metadata"]["connections"]["comments"].total,
        user: clip["user"].name,
        userLink: clip["user"].link,
        pictures: clip["pictures"]["sizes"][largestImage].link
    }
}

export function fuzzySearch(videos,string) {
    if(string.length === 0 ) return
    return videos.filter((video) => `${video.name} ${video.description}`.indexOf(string.trim()) > -1)
}


// sorting logic

export function sort(results, sortType) {

    if(!results) return

    if(sortType === "Popularity"){
        results.sort((a,b) => { 
            return (a.plays > b.plays) ? -1 : 1   //had to use plays because # favorites was not provided in example data 
        })
    } 
    if(sortType === "TitleAZ"){
        results.sort((a,b) => { 
            return (a.name.toLowerCase() > b.name.toLowerCase()) ? 1 : -1
        })
    } 
    if(sortType === "TitleZA"){
        results.sort((a,b) => { 
            return (a.name.toLowerCase() > b.name.toLowerCase()) ? -1 : 1
        })
    } 
    if(sortType === "Longest"){
        results.sort((a,b) => { 
            return (a.duration > b.duration) ? -1 : 1
        })
    } 
    if(sortType === "Shortest"){
        results.sort((a,b) => { 
            return (a.duration > b.duration) ? 1 : -1
        })
    } 
    if(sortType === "Newest"){
        results.sort((a,b) => { 
            return (a.created > b.created) ? -1 : 1
        })
    } 
    if(sortType === "Oldest"){
        results.sort((a,b) => { 
            return (a.created > b.created) ? 1 : -1
        })
    } 
    if(sortType === "UserAZ"){
        results.sort((a,b) => { 
            return (a.user.toLowerCase() > b.user.toLowerCase()) ? 1 : -1
        })
    } 
    if(sortType === "UserZA"){
        results.sort((a,b) => { 
            return (a.user.toLowerCase() > b.user.toLowerCase()) ? -1 : 1
        })
    } 
    return results
}

