        if(currentFilter === "365" && resultsExist) {
            return currentResults.filter(result => rightNow - Date.parse(result.created) <  year)
        }
        if(currentFilter === "30" && resultsExist) {
           return currentResults.filter(result => rightNow - Date.parse(result.created) <  month)
        }
        if(currentFilter === "7" && resultsExist) {
          return currentResults.filter(result => rightNow - Date.parse(result.created) <  week)
        }
    }

    const filterDuration = (currentResults, currentFilter) => {

        if (currentFilter === "anyDuration") {
            return currentResults
        }
        if (currentFilter === "short" && resultsExist) {
            return currentResults.filter(result => result.duration < 240)
        }
        if (currentFilter === "med" && resultsExist) {
            return currentResults.filter(result => result.duration > 240 && result.duration < 600)
        }
        if (currentFilter === "long" && resultsExist) {
            return currentResults.filter(result => result.duration > 600)
        }
    }

    const filterFeatured = (currentResults, currentFilter) => {

        if(currentFilter === "anyFeatured" && resultsExist) {
            return currentResults
        } 
        if(currentFilter === "featured" && resultsExist) {
           return currentResults.filter(result => (result.featured))
        }
    }

    //Initially, I tried to write a filtering function that took the filtering values all at once. This ended up not working,
