    } else {
        const numResults = table[param.toLowerCase()].total
        const s = numResults.toString()
        if (numResults > 999999 && numResults < 10000000 ) {
            return s[0] + "." + s[1] + s[2] + "M"
        } 
        if (numResults > 9999999 && numResults < 100000000) {
            return s[0] + s[1] + "." + s[2] + s[3] + "M"
        }
        if (numResults > 99999999) {
            return s[0] + s[1] + s[2] + "." + s[3] + s[4] + "M"
        } else {
            return s
        }
    }
}

export function formatTime (seconds) {
    if(seconds < 0 || isNaN(seconds)) return null
    const duration = new Date(null)
    duration.setSeconds(seconds)
    const result = duration.toISOString().substr(11, 8)
    return seconds >= 3600  ? result : result.slice(3)
}
