import React, { Component } from 'react'
import styled from 'styled-components'
import QueryData from './data'


export class Serp extends Component {
    constructor(props) {
        super(props)

        this.state = {
             value: '',
             currentSearchParam: null,
             resultNum: 9000,
             hasBeenSearched: false,
             results: ['this', 'is', 'a', 'test']
        }
    }

    onChange = (e) => {
        this.setState({ value: e.target.value})
    }

    onSubmit = (e) => {
        e.preventDefault()
        if(this.state.value.length === 0) {
            alert('Redirected to https://vimeo.com/search?q=')
        } else {
            console.log(this.state.value)

            // call function that queries database 

            this.setState({
                currentSearchParam:this.state.value,
                hasBeenSearched: true
            })
        }
    }



    render() {

        const Button = styled.button`
            background: transparent;
            border-radius: 3px;
            border: 2px solid palevioletred;
            color: palevioletred;
            margin: 0 1em;
            padding: 0.25em 1em;
            `

        const hasBeenSearched = this.state.hasBeenSearched
        const param = this.state.currentSearchParam
        const resultNum = this.state.resultNum
        const results = this.state.results


        let searched = (hasBeenSearched) ? <span className="result-summary"> {results} results for "{param}" </span> : <span>  </span> 


        return (
            <div className="container">
                <div className="header">
                        {searched} 
                    <div className="search-box">
                        <form onSubmit={this.onSubmit}> 
                            <input  onChange={this.onChange}
                                    id="input-field"
                                    placeholder="Search videos, people, and more"
                                    type="text"
                                    value={this.state.value}/>
                            <Button onSubmit={this.onSubmit}>hello</Button>
                        </form>


                    </div>
                </div>

                <div className="result-body">
                    {results}
                </div>
            </div>
        )
    }
}

export default Serp
