import React, { Component } from 'react'
import {parse, formatTime} from './functions'
import queryData from './data/aggregate.json'

import styled from 'styled-components'
import {Comment} from '@styled-icons/boxicons-solid/Comment'
import {Play} from '@styled-icons/boxicons-regular/Play'
import Moment from 'react-moment'
import 'bootstrap/dist/css/bootstrap.min.css';
import { Row, Col, Container, Navbar} from 'react-bootstrap'


export class Serp extends Component {
    constructor(props) {
        super(props)

        this.state = {
             value: '',
             currentSearchParam: null,
             resultNum: null,
             hasBeenSearched: false,
             results: []
        }
    }

    onChange = (e) => {
        this.setState({ value: e.target.value})
    }

    onSubmit = async (e) => {
        e.preventDefault()
        if(this.state.value.length === 0) {
            alert('Redirected to https://vimeo.com/search?q=')
        } else {

            this.setState({
                currentSearchParam: this.state.value,
                hasBeenSearched: true,
                results: await parse(queryData),
                resultNum: results.length
            })
        }
    }


    render() {

        const Button = styled.button`
            background: transparent;
            border-radius: 3px;
            border: 2px solid palevioletred;
            color: palevioletred;
            margin: 0 1em;
            padding: 0.25em 1em;
        `
        const Whitecomment = styled(Comment)`
            padding-right: 5px; 
            color: white;
            height: 60%;
            width: auto;
        `

        const Whiteplay = styled(Play)`
            padding-bottom: 3px;
            color: white;
            height: 100%;
            width: auto;
        `

        const hasBeenSearched = this.state.hasBeenSearched
        const param = this.state.currentSearchParam 
        const resultNum = this.state.resultNum
        const results = this.state.results


        const resultsMap = results.map((video) => {
            const duration = formatTime(video.duration)
            return (
                <Col xl={4} lg={4} md={6} sm={6} xs={12} className="p-0 m-0" key={video.videoID}> 
                    <div className="result-box" >  
                        <div className="thumbnail-wrapper">
                            <div className="detail-container"> 
                                <div className="hover-details">

                                    <div className="ago"> 
                                        <span> <Moment fromNow ago>{video.created}</Moment> ago </span>
                                    </div>


                                    <div className="plays"> 
                                        <Whiteplay />
                                        <span>{video.plays}</span>
                                    </div>


                                    <div className="comments">
                                        <Whitecomment />
                                        <span>{video.comments}</span>
                                    </div>

                                    <div className="duration">
                                        <span>{duration}</span>
                                    </div>

                                </div>
                            </div>

                            <a href={video.URLformat}>
                                <img src={video.pictures} className="thumbnail" alt="thumbnail"/>
                            </a>

                        </div>

                        <div className="info-container"> 
                            <div className="video-title"> 
                                <a href={video.URLformat} target="_blank">{video.name}</a> 
                            </div>
                            <div className="user-link"> 
                                <a href={video.userLink}> {video.user}</a> 
                            </div>   
                        </div>
                    </div>
                </Col>
            )
        })

        let searched = (hasBeenSearched) ? <span className="result-summary"> {resultNum} results for "{param}" </span> : <span>  </span> 

        return (
            <Container fluid>
                <Row>
                    <Col md= {3} sm={0} xs={0}>
                        {/* filler col space  */}
                    </Col>

                        <Col xl={8} lg={9} md= {9} sm={12} xs={12} className="p-0">
                            <div className="float-left"> 
                                {searched} 
                            </div>
                            <div className="search-box float-right">
                                    <form onSubmit={this.onSubmit}> 
                                        <input  onChange={this.onChange}
                                                id="input-field"
                                                placeholder="Search videos, people, and more"
                                                type="text"
                                                value={this.state.value}/>
                                        <Button onSubmit={this.onSubmit}>hello</Button>
                                    </form>
                            </div>
                        </Col>

                    <Col xl={1} lg={0}>
                        {/* filler col space */}
                    </Col>
                </Row>


                <Row>
                    <Col xl ={3} lg={3} md= {3} sm={0} xs={0}> 
                        <Container>
                            <input type="radio"   />
                        </Container>
                    </Col> 

                    <Col xl={8} lg={9} md= {9} sm={12} xs={12}> 
                        <Row>
                            {resultsMap} 
                        </Row>
                    </Col>

                    <Col xl={1} lg={0} md= {0} sm={0} xs={0}> 
                         {/* filler space col  */}
                    </Col>
                </Row>
            </Container>
        )
    }
}

export default Serp
